import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useNavigate,
  useParams,
  Link,
} from "react-router-dom";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Card,
  CardContent,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  AppBar,
  Toolbar,
} from "@mui/material";

// --------------------- Custom Logger ---------------------
const logger = {
  logs: [],
  log(message) {
    const entry = `${new Date().toISOString()} - ${message}`;
    this.logs.push(entry);
    localStorage.setItem("logs", JSON.stringify(this.logs));
  },
  getLogs() {
    return JSON.parse(localStorage.getItem("logs") || "[]");
  },
};

// --------------------- URL Storage ---------------------
const getStoredUrls = () => JSON.parse(localStorage.getItem("urls") || "[]");

const saveUrl = (urlData) => {
  const urls = getStoredUrls();
  urls.push(urlData);
  localStorage.setItem("urls", JSON.stringify(urls));
};

const updateUrlStats = (code, clickData) => {
  const urls = getStoredUrls();
  const updated = urls.map((u) =>
    u.code === code ? { ...u, clicks: [...u.clicks, clickData] } : u
  );
  localStorage.setItem("urls", JSON.stringify(updated));
};

// --------------------- URL Shortener Page ---------------------
function UrlShortenerPage() {
  const [urls, setUrls] = useState([
    { longUrl: "", validity: "", shortcode: "" },
  ]);
  const [results, setResults] = useState(getStoredUrls());

  const handleChange = (i, field, value) => {
    const updated = [...urls];
    updated[i][field] = value;
    setUrls(updated);
  };

  const addUrlField = () => {
    if (urls.length < 5) {
      setUrls([...urls, { longUrl: "", validity: "", shortcode: "" }]);
    }
  };

  const shortenUrls = () => {
    const existingCodes = new Set(getStoredUrls().map((u) => u.code));
    const newResults = [];

    urls.forEach((u) => {
      if (!u.longUrl.match(/^https?:\/\/.+/)) {
        logger.log(`Invalid URL skipped: ${u.longUrl}`);
        return;
      }
      let code = u.shortcode || Math.random().toString(36).substring(2, 7);
      while (existingCodes.has(code)) {
        code = Math.random().toString(36).substring(2, 7);
      }
      const validity = parseInt(u.validity) || 30;
      const expiry = new Date(Date.now() + validity * 60000);
      const urlData = { ...u, code, expiry, clicks: [] };
      saveUrl(urlData);
      newResults.push(urlData);
      logger.log(`Shortened ${u.longUrl} -> ${code}`);
    });

    setResults(getStoredUrls());
  };

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 2 }}>
        URL Shortener
      </Typography>
      {urls.map((u, i) => (
        <Box key={i} sx={{ mb: 2 }}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={5}>
              <TextField
                fullWidth
                label="Long URL"
                value={u.longUrl}
                onChange={(e) => handleChange(i, "longUrl", e.target.value)}
              />
            </Grid>
            <Grid item xs={12} sm={3}>
              <TextField
                fullWidth
                label="Validity (minutes)"
                value={u.validity}
                onChange={(e) => handleChange(i, "validity", e.target.value)}
              />
            </Grid>
            <Grid item xs={12} sm={4}>
              <TextField
                fullWidth
                label="Custom Shortcode"
                value={u.shortcode}
                onChange={(e) => handleChange(i, "shortcode", e.target.value)}
              />
            </Grid>
          </Grid>
        </Box>
      ))}
      <Button variant="contained" onClick={addUrlField} sx={{ mr: 2 }}>
        + Add URL
      </Button>
      <Button variant="contained" color="success" onClick={shortenUrls}>
        Shorten
      </Button>

      <Box sx={{ mt: 4 }}>
        <Typography variant="h6">Results</Typography>
        {results.map((r, i) => (
          <Card key={i} sx={{ my: 1 }}>
            <CardContent>
              <Typography>
                Long: {r.longUrl}
                <br />
                Short:{" "}
                <Link to={`/${r.code}`} target="_blank">
                  {window.location.origin}/{r.code}
                </Link>
                <br />
                Expiry: {new Date(r.expiry).toLocaleString()}
              </Typography>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Container>
  );
}

// --------------------- Redirect Handler ---------------------
function RedirectPage() {
  const { code } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const urls = getStoredUrls();
    const match = urls.find((u) => u.code === code);
    if (match) {
      if (new Date() > new Date(match.expiry)) {
        logger.log(`Expired link attempted: ${code}`);
        alert("This link has expired!");
        navigate("/");
      } else {
        const clickData = {
          timestamp: new Date().toISOString(),
          source: document.referrer || "Direct",
          location: "Unknown", // Can be enhanced with geolocation API
        };
        updateUrlStats(code, clickData);
        logger.log(`Redirecting ${code} -> ${match.longUrl}`);
        window.location.href = match.longUrl;
      }
    } else {
      logger.log(`Invalid shortcode: ${code}`);
      alert("Invalid short link!");
      navigate("/");
    }
  }, [code, navigate]);

  return <Typography>Redirecting...</Typography>;
}

// --------------------- Statistics Page ---------------------
function StatsPage() {
  const [urls, setUrls] = useState(getStoredUrls());

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 2 }}>
        URL Statistics
      </Typography>
      {urls.map((u, i) => (
        <Card key={i} sx={{ my: 2 }}>
          <CardContent>
            <Typography variant="h6">
              {window.location.origin}/{u.code}
            </Typography>
            <Typography>
              Created: {new Date(u.expiry).toLocaleString()}
              <br />
              Expiry: {new Date(u.expiry).toLocaleString()}
              <br />
              Total Clicks: {u.clicks.length}
            </Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Timestamp</TableCell>
                  <TableCell>Source</TableCell>
                  <TableCell>Location</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {u.clicks.map((c, idx) => (
                  <TableRow key={idx}>
                    <TableCell>{c.timestamp}</TableCell>
                    <TableCell>{c.source}</TableCell>
                    <TableCell>{c.location}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      ))}
    </Container>
  );
}

