import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useParams,
  useNavigate,
  Link,
} from "react-router-dom";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Card,
  CardContent,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  AppBar,
  Toolbar,
} from "@mui/material";

/* -------------------- Custom Logger -------------------- */
const AppLogger = {
  records: [],
  write(message) {
    const logEntry = `${new Date().toLocaleString()} | ${message}`;
    this.records.push(logEntry);
    localStorage.setItem("app_logs", JSON.stringify(this.records));
  },
  read() {
    return JSON.parse(localStorage.getItem("app_logs") || "[]");
  },
};

/* -------------------- Storage Helpers -------------------- */
const fetchLinks = () =>
  JSON.parse(localStorage.getItem("short_links") || "[]");

const storeLink = (entry) => {
  const allLinks = fetchLinks();
  allLinks.push(entry);
  localStorage.setItem("short_links", JSON.stringify(allLinks));
};

const recordClick = (code, clickInfo) => {
  const links = fetchLinks();
  const updated = links.map((item) =>
    item.code === code ? { ...item, clicks: [...item.clicks, clickInfo] } : item
  );
  localStorage.setItem("short_links", JSON.stringify(updated));
};

/* -------------------- Shortener Page -------------------- */
function Shortener() {
  const [inputs, setInputs] = useState([{ url: "", minutes: "", custom: "" }]);
  const [data, setData] = useState(fetchLinks());

  const updateInput = (index, field, val) => {
    const copy = [...inputs];
    copy[index][field] = val;
    setInputs(copy);
  };

  const addField = () => {
    if (inputs.length < 5) {
      setInputs([...inputs, { url: "", minutes: "", custom: "" }]);
    }
  };

  const generateLinks = () => {
    const existing = new Set(fetchLinks().map((x) => x.code));
    const newLinks = [];

    inputs.forEach((item) => {
      if (!item.url.match(/^https?:\/\/.+/)) {
        AppLogger.write(`Skipped invalid URL: ${item.url}`);
        return;
      }

      let code = item.custom || Math.random().toString(36).slice(2, 7);
      while (existing.has(code)) {
        code = Math.random().toString(36).slice(2, 7);
      }

      const minutes = parseInt(item.minutes) || 30;
      const expiry = new Date(Date.now() + minutes * 60 * 1000);

      const entry = { ...item, code, expiry, clicks: [] };
      storeLink(entry);
      newLinks.push(entry);

      AppLogger.write(`Created short link: ${code} for ${item.url}`);
    });

    setData(fetchLinks());
  };

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 2 }}>
        Shorten URLs
      </Typography>
      {inputs.map((field, idx) => (
        <Box key={idx} sx={{ mb: 2 }}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={5}>
              <TextField
                fullWidth
                label="Original URL"
                value={field.url}
                onChange={(e) => updateInput(idx, "url", e.target.value)}
              />
            </Grid>
            <Grid item xs={12} sm={3}>
              <TextField
                fullWidth
                label="Validity (minutes)"
                value={field.minutes}
                onChange={(e) => updateInput(idx, "minutes", e.target.value)}
              />
            </Grid>
            <Grid item xs={12} sm={4}>
              <TextField
                fullWidth
                label="Custom code"
                value={field.custom}
                onChange={(e) => updateInput(idx, "custom", e.target.value)}
              />
            </Grid>
          </Grid>
        </Box>
      ))}
      <Button onClick={addField} variant="outlined" sx={{ mr: 2 }}>
        + Add more
      </Button>
      <Button onClick={generateLinks} variant="contained" color="success">
        Shorten
      </Button>

      <Box sx={{ mt: 4 }}>
        <Typography variant="h6">Generated Links</Typography>
        {data.map((item, i) => (
          <Card key={i} sx={{ my: 1 }}>
            <CardContent>
              <Typography>
                <strong>Original:</strong> {item.url}
                <br />
                <strong>Short:</strong>{" "}
                <Link to={`/${item.code}`} target="_blank">
                  {window.location.origin}/{item.code}
                </Link>
                <br />
                <strong>Expires:</strong>{" "}
                {new Date(item.expiry).toLocaleString()}
              </Typography>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Container>
  );
}

/* -------------------- Redirect Handler -------------------- */
function Redirect() {
  const { code } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const links = fetchLinks();
    const match = links.find((x) => x.code === code);

    if (!match) {
      AppLogger.write(`Attempted invalid code: ${code}`);
      alert("Invalid link!");
      navigate("/");
      return;
    }

    if (new Date() > new Date(match.expiry)) {
      AppLogger.write(`Expired link used: ${code}`);
      alert("This link has expired.");
      navigate("/");
      return;
    }

    const clickInfo = {
      at: new Date().toLocaleString(),
      ref: document.referrer || "Direct",
      place: "Unknown", // can extend with navigator.geolocation
    };
    recordClick(code, clickInfo);
    AppLogger.write(`Redirected ${code} to ${match.url}`);

    window.location.href = match.url;
  }, [code, navigate]);

  return <Typography>Redirecting...</Typography>;
}

/* -------------------- Stats Page -------------------- */
function Stats() {
  const [records] = useState(fetchLinks());

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 2 }}>
        Link Statistics
      </Typography>
      {records.map((item, i) => (
        <Card key={i} sx={{ my: 2 }}>
          <CardContent>
            <Typography variant="h6">
              {window.location.origin}/{item.code}
            </Typography>
            <Typography>
              Expiry: {new Date(item.expiry).toLocaleString()} <br />
              Clicks: {item.clicks.length}
            </Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Time</TableCell>
                  <TableCell>Referrer</TableCell>
                  <TableCell>Location</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {item.clicks.map((c, j) => (
                  <TableRow key={j}>
                    <TableCell>{c.at}</TableCell>
                    <TableCell>{c.ref}</TableCell>
                    <TableCell>{c.place}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      ))}
    </Container>
  );
}

/* -------------------- Log Viewer -------------------- */
function Logs() {
  const [logs] = useState(AppLogger.read());

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 2 }}>
        System Logs
      </Typography>
      {logs.map((log, i) => (
        <Typography key={i}>{log}</Typography>
      ))}
    </Container>
  );
}

